import { FC, useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import './CurrencyExchange.css'

export const CurrencyExchange: FC = () => {
  const [state, setState] = useState('')
  const [total, setTotal] = useState(0)
  const [value, setValue] = useState('')

  const int = parseInt(state)
  console.log(int)
  if (value === 'UAH') {
  }
  console.log(total, state)

  const currentValue = localStorage.getItem('currency')

  const navigate = useNavigate()
  const goBack = () => navigate('/', { replace: true })
  useEffect(() => {
    setValue(currentValue || '')
  }, [currentValue])

  return (
    <div>
      <div className="go-back" onClick={goBack}>
        Go back
      </div>
      <>
        <label className="sum-label">Sum</label>
        <input
          className="sum-money"
          placeholder="Enter your sum of money"
          name="sum-money"
          type="text"
          value={state}
          onChange={e => setState(e.target.value)}
        />
      </>
      <div className="total">
        <span className="total-title">You'll can get in {currentValue}: </span>
        <span className="total-sum">{total}</span>
      </div>
    </div>
  )
}
